mapper.select:
select count(*) as cnt
from tb_attachFile
where member_no = #{memberNo}

select count(*) as cnt
from tb_resume
where member_no = #{memberNo}

mapper.update:
update tb_resume_photo
   set ori_name = #{oriName},
       ser_name = #{serName},
       ser_path = #{serPath}
 where resume_no = #{resumeNo}
